# Autogenerated wrapper script for Dex_jll for x86_64-linux-gnu
export grpcclient, dex, exampleapp

## Global variables
PATH = ""
LIBPATH = ""
LIBPATH_env = "LD_LIBRARY_PATH"

# Relative path to `grpcclient`
const grpcclient_splitpath = ["bin", "grpc-client"]

# This will be filled out by __init__() for all products, as it must be done at runtime
grpcclient_path = ""

# grpcclient-specific global declaration
function grpcclient(f::Function; adjust_PATH::Bool = true, adjust_LIBPATH::Bool = true)
    global PATH, LIBPATH
    env_mapping = Dict{String,String}()
    if adjust_PATH
        if !isempty(get(ENV, "PATH", ""))
            env_mapping["PATH"] = string(PATH, ':', ENV["PATH"])
        else
            env_mapping["PATH"] = PATH
        end
    end
    if adjust_LIBPATH
        if !isempty(get(ENV, LIBPATH_env, ""))
            env_mapping[LIBPATH_env] = string(LIBPATH, ':', ENV[LIBPATH_env])
        else
            env_mapping[LIBPATH_env] = LIBPATH
        end
    end
    withenv(env_mapping...) do
        f(grpcclient_path)
    end
end


