"""
Open all libraries
"""
function __init__()
    global artifact_dir = abspath(artifact"Dex")

    # Initialize PATH and LIBPATH environment variable listings
    global PATH_list, LIBPATH_list
    # We first need to add to LIBPATH_list the libraries provided by Julia
    append!(LIBPATH_list, [joinpath(Sys.BINDIR, Base.LIBDIR, "julia"), joinpath(Sys.BINDIR, Base.LIBDIR)])
    global grpcclient_path = normpath(joinpath(artifact_dir, grpcclient_splitpath...))

    push!(PATH_list, dirname(grpcclient_path))
    global dex_path = normpath(joinpath(artifact_dir, dex_splitpath...))

    push!(PATH_list, dirname(dex_path))
    global exampleapp_path = normpath(joinpath(artifact_dir, exampleapp_splitpath...))

    push!(PATH_list, dirname(exampleapp_path))
    # Filter out duplicate and empty entries in our PATH and LIBPATH entries
    filter!(!isempty, unique!(PATH_list))
    filter!(!isempty, unique!(LIBPATH_list))
    global PATH = join(PATH_list, ':')
    global LIBPATH = join(LIBPATH_list, ':')

    # Add each element of LIBPATH to our DL_LOAD_PATH (necessary on platforms
    # that don't honor our "already opened" trick)
    #for lp in LIBPATH_list
    #    push!(DL_LOAD_PATH, lp)
    #end
end  # __init__()

